import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

function App() {
  const [weatherData, setWeatherData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Ankara'nın koordinatları
    const lat = 39.9334;
    const lon = 32.8597;
    const apiKey = "b9c4cfc5fb5c3c2c530f6714a8e9c89f";

    // Hava durumu API'sine istek gönderin
    axios
      .get(`https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&appid=${apiKey}`)
      .then((response) => {
        // API'den gelen veriyi alın
        const hourlyData = response.data.hourly.map((hour) => ({
          time: new Date(hour.dt * 1000).toLocaleTimeString(),
          temperature: Math.round(hour.temp - 273.15), // Kelvin'i Celsius'a çevir
        }));
        setWeatherData(hourlyData);
        setLoading(false);
      })
      .catch((error) => {
        console.error("API isteği sırasında hata oluştu:", error);
      });
  }, []);

  return (
    <div className="App">
      <h1>Ankara Hava Durumu</h1>
      {loading ? (
        <p>Veri yükleniyor...</p>
      ) : (
        <ResponsiveContainer width="100%" height={400}>
          <LineChart data={weatherData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="time" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line
              type="monotone"
              dataKey="temperature"
              name="Sıcaklık (°C)"
              stroke="#8884d8"
            />
          </LineChart>
        </ResponsiveContainer>
      )}
    </div>
  );
}

export default App;
