import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

function App() {
  const [weatherData, setWeatherData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const apiKey = b9c4cfc5fb5c3c2c530f6714a8e9c89f; // OpenWeather API anahtarınızı buraya ekleyin
    const cityName = "Ankara";

    // Hava durumu API'sine istek gönderin
    axios
      .get(`https://api.openweathermap.org/data/2.5/weather?q=${cityName}&appid=${apiKey}`)
      .then((response) => {
        // API'den gelen hava durumu verilerini alın
        setWeatherData(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("API isteği sırasında hata oluştu:", error);
        setLoading(false);
      });
  }, []);

  return (
    <div className="App">
      <h1>Ankara Hava Durumu</h1>
      {loading ? (
        <p>Veri yükleniyor...</p>
      ) : (
        <div>
          <p>Şehir: {weatherData.name}</p>
          <p>Hava Durumu: {weatherData.weather[0].description}</p>
          <p>Sıcaklık: {Math.round(weatherData.main.temp - 273.15)}°C</p> {/* Kelvin'i Celsius'a çevirin */}
          <p>Rüzgar Hızı: {weatherData.wind.speed} m/s</p>
          <p>Rüzgar Yönü: {weatherData.wind.deg}°</p>
        </div>
      )}

      {weatherData.main && (
        <div>
          <h2>Saatlik Sıcaklık Değişimi</h2>
          <ResponsiveContainer width="100%" height={400}>
            <LineChart data={weatherData.main.temp_hourly}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="time" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line
                type="monotone"
                dataKey="temperature"
                name="Sıcaklık (°C)"
                stroke="#8884d8"
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
}

export default App;
