import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

function App() {
  const [weatherData, setWeatherData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const apiKey = "b9c4cfc5fb5c3c2c530f6714a8e9c89f"; // OpenWeather API anahtarınızı buraya ekleyin
    const cityName = "Ankara";

    // Geçmiş 1 haftalık hava durumu verilerini almak için API'ye istek gönderin
    axios
      .get(`https://api.openweathermap.org/data/2.5/forecast?q=${cityName}&appid=${apiKey}`)
      .then((response) => {
        // API'den gelen hava durumu verilerini alın
        setWeatherData(response.data.list);
        setLoading(false);
      })
      .catch((error) => {
        console.error("API isteği sırasında hata oluştu:", error);
        setLoading(false);
      });
  }, []);

  return (
    <div className="App">
      <h1>Ankara Hava Durumu (Geçmiş 1 Hafta)</h1>
      {loading ? (
        <p>Veri yükleniyor...</p>
      ) : (
        <div>
          <ResponsiveContainer width="100%" height={400}>
            <LineChart data={weatherData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="dt_txt" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line
                type="monotone"
                dataKey="main.temp"
                name="Sıcaklık (°K)"
                stroke="#8884d8"
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
}

export default App;
